const rect = {
  x: -2000,
  y: -2000,
  w: 30,
  h: 30
};

// default state
let isPenDown = true;
// default colour
let drawColor = "cd0000";

//Initialising the canvas
function init() {
  canvas = document.getElementById("myCanvas");
  var ctx = canvas.getContext("2d");
  canvas.width = window.innerWidth * 0.8;
  canvas.height = window.innerHeight * 0.8;
  var img = new Image();
  img.crossOrigin = "Anonymous";
  img.onload = function() {
    ctx.drawImage(img, 0, 0);
  };
  img.src =
    "data:image/png;base64,";
  canvas.focus();
  canvas.addEventListener("touchmove", this.onMove);
  canvas.addEventListener("touchstart", this.onStart);
  canvas.addEventListener("mousemove", this.onMove);
  canvas.addEventListener("mousedown", this.onStart);
  canvas.addEventListener("keydown", this.doKeyDown);
  canvas.addEventListener("click", this.colorPick);
  draw();
}

function rgbToHex(R, G, B) {
  return toHex(R) + toHex(G) + toHex(B);
}

function toHex(n) {
  n = parseInt(n, 10);
  if (isNaN(n)) return "00";
  n = Math.max(0, Math.min(n, 255));
  return (
    "0123456789ABCDEF".charAt((n - (n % 16)) / 16) +
    "0123456789ABCDEF".charAt(n % 16)
  );
}

function colorPick(e) {
  canvas = document.getElementById("myCanvas");
  var ctx = canvas.getContext("2d");
  var imgData = ctx.getImageData(e.clientX, e.clientY, 1, 1);
  drawColor = rgbToHex(imgData.data[0], imgData.data[1], imgData.data[2]);
  event.preventDefault();
}

function doKeyDown(e) {
  if (e.keyCode == 66) {
    drawColor = "0000FF";
  } else if (e.keyCode == 71) {
    drawColor = "008000";
  } else if (e.keyCode == 82) {
    drawColor = "FF0000";
  } else if (e.keyCode == 89) {
    drawColor = "FFFF00";
  } else if (e.keyCode == 38) {
    isPenDown = false;
  } else if (e.keyCode == 40) {
    isPenDown = true;
  } else if (e.keyCode == 32) {
    clearAll();
  }
}

function onStart(event) {
  const touch = event.targetTouches ? event.targetTouches[0] : event;
  const x = event.targetTouches ? touch.pageX : event.x;
  const y = event.targetTouches ? touch.pageY : event.y;
  // excluding the area of colour picker
  if (!(x < 220 && y < 220) && isPenDown) {
    rect.x = x - 20;
    rect.y = y - 20;
    draw();
  }

  event.preventDefault();
}

function onMove(event) {
  const touch = event.targetTouches ? event.targetTouches[0] : event;
  const x = event.targetTouches ? touch.pageX : event.x;
  const y = event.targetTouches ? touch.pageY : event.y;
    // excluding the area of colour picker
  if (!(x < 220 && y < 220) && isPenDown) {
    rect.x = x - 20;
    rect.y = y - 20;
    draw();
  }
  event.preventDefault();
}

function draw() {
  canvas = document.getElementById("myCanvas");
  var ctx = canvas.getContext("2d");
  ctx.fillStyle = "#" + drawColor;
  ctx.fillRect(rect.x, rect.y, rect.w, rect.h);
  canvas.focus();
}

function clearAll() {
  canvas = document.getElementById("myCanvas");
  var ctx = canvas.getContext("2d");
  ctx.clearRect(0, 0, canvas.width, canvas.height);
}

function resizeCanvas() {
  canvas = document.getElementById("myCanvas");
  canvas.width = window.innerWidth * 0.8;
  canvas.height = window.innerHeight * 0.8;
}

init();
/**
 * resizing and orientation change event handlers
 */
window.onresize = resizeCanvas;
window.onorientationchange = resizeCanvas;
